<script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
<script>
document.addEventListener('DOMContentLoaded', function() {
    const dailyData = @json($dailyRevenue);
    const weeklyData = @json($weeklyRevenue);
    const monthlyData = @json($monthlyRevenue);

    const isDarkMode = document.documentElement.classList.contains('dark');
    const textColor = isDarkMode ? '#9CA3AF' : '#6B7280';
    const gridColor = isDarkMode ? '#374151' : '#E5E7EB';

    const chartOptions = {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                display: false
            },
            tooltip: {
                backgroundColor: isDarkMode ? '#1F2937' : '#FFFFFF',
                titleColor: isDarkMode ? '#F3F4F6' : '#111827',
                bodyColor: isDarkMode ? '#D1D5DB' : '#4B5563',
                borderColor: isDarkMode ? '#374151' : '#E5E7EB',
                borderWidth: 1,
                callbacks: {
                    label: function(context) {
                        return '$' + context.parsed.y.toLocaleString(undefined, {minimumFractionDigits: 2, maximumFractionDigits: 2});
                    }
                }
            }
        },
        scales: {
            x: {
                grid: {
                    color: gridColor,
                    drawBorder: false
                },
                ticks: {
                    color: textColor
                }
            },
            y: {
                grid: {
                    color: gridColor,
                    drawBorder: false
                },
                ticks: {
                    color: textColor,
                    callback: function(value) {
                        return '$' + value.toLocaleString();
                    }
                }
            }
        }
    };

    const ctx = document.getElementById('revenueChart').getContext('2d');
    let revenueChart = new Chart(ctx, {
        type: 'line',
        data: {
            labels: dailyData.map(d => d.date),
            datasets: [{
                label: 'Revenue',
                data: dailyData.map(d => d.total),
                borderColor: '#6366F1',
                backgroundColor: 'rgba(99, 102, 241, 0.1)',
                fill: true,
                tension: 0.4,
                pointRadius: 4,
                pointHoverRadius: 6
            }]
        },
        options: chartOptions
    });

    function updateChart(data, labels) {
        revenueChart.data.labels = labels;
        revenueChart.data.datasets[0].data = data.map(d => d.total);
        revenueChart.update();
    }

    const dailyBtn = document.getElementById('dailyBtn');
    const weeklyBtn = document.getElementById('weeklyBtn');
    const monthlyBtn = document.getElementById('monthlyBtn');

    function setActiveButton(activeBtn) {
        [dailyBtn, weeklyBtn, monthlyBtn].forEach(btn => {
            btn.classList.remove('bg-indigo-600', 'text-white');
            btn.classList.add('bg-gray-200', 'dark:bg-gray-700', 'text-gray-700', 'dark:text-gray-300');
        });
        activeBtn.classList.remove('bg-gray-200', 'dark:bg-gray-700', 'text-gray-700', 'dark:text-gray-300');
        activeBtn.classList.add('bg-indigo-600', 'text-white');
    }

    dailyBtn.addEventListener('click', function() {
        setActiveButton(this);
        updateChart(dailyData, dailyData.map(d => d.date));
    });

    weeklyBtn.addEventListener('click', function() {
        setActiveButton(this);
        updateChart(weeklyData, weeklyData.map(d => d.label));
    });

    monthlyBtn.addEventListener('click', function() {
        setActiveButton(this);
        updateChart(monthlyData, monthlyData.map(d => d.label));
    });

    const transactionCtx = document.getElementById('transactionChart').getContext('2d');
    new Chart(transactionCtx, {
        type: 'line',
        data: {
            labels: dailyData.map(d => d.date),
            datasets: [{
                label: 'Transactions',
                data: dailyData.map(d => d.count),
                borderColor: '#10B981',
                backgroundColor: 'rgba(16, 185, 129, 0.1)',
                fill: true,
                tension: 0.4,
                pointRadius: 3
            }]
        },
        options: {
            ...chartOptions,
            scales: {
                ...chartOptions.scales,
                y: {
                    ...chartOptions.scales.y,
                    ticks: {
                        color: textColor,
                        callback: function(value) {
                            return value.toLocaleString();
                        }
                    }
                }
            },
            plugins: {
                ...chartOptions.plugins,
                tooltip: {
                    ...chartOptions.plugins.tooltip,
                    callbacks: {
                        label: function(context) {
                            return context.parsed.y.toLocaleString() + ' transactions';
                        }
                    }
                }
            }
        }
    });

    const monthlyBarCtx = document.getElementById('monthlyBarChart').getContext('2d');
    new Chart(monthlyBarCtx, {
        type: 'bar',
        data: {
            labels: monthlyData.map(d => d.label),
            datasets: [{
                label: 'Revenue',
                data: monthlyData.map(d => d.total),
                backgroundColor: 'rgba(99, 102, 241, 0.8)',
                borderColor: '#6366F1',
                borderWidth: 1,
                borderRadius: 4
            }]
        },
        options: chartOptions
    });
});
</script>
@endsection

