@layer utilities {
    .loading {
        @apply relative pointer-events-none;
    }

    .loading::after {
        content: '';
        @apply absolute inset-0 bg-white/70 dark:bg-gray-900/70 backdrop-blur-sm 
               flex items-center justify-center rounded-inherit;
    }

    .loading::before {
        content: '';
        @apply absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-8 h-8 
               border-4 border-primary-200 border-t-primary-600 rounded-full animate-spin z-10;
    }

    .page-enter {
        animation: pageEnter 0.4s cubic-bezier(0.4, 0, 0.2, 1) forwards;
    }

    @keyframes pageEnter {
        from {
            opacity: 0;
            transform: translateY(12px);
        }
        to {
            opacity: 1;
            transform: translateY(0);
        }
    }

    .fade-in {
        animation: fadeIn 0.3s ease-out forwards;
    }

    @keyframes fadeIn {
        from { opacity: 0; }
        to { opacity: 1; }
    }

    .slide-up {
        animation: slideUp 0.4s cubic-bezier(0.4, 0, 0.2, 1) forwards;
    }

    @keyframes slideUp {
        from {
            opacity: 0;
            transform: translateY(24px);
        }
        to {
            opacity: 1;
            transform: translateY(0);
        }
    }

    .scale-in {
        animation: scaleIn 0.2s ease-out forwards;
    }

    @keyframes scaleIn {
        from {
            opacity: 0;
            transform: scale(0.95);
        }
        to {
            opacity: 1;
            transform: scale(1);
        }
    }

    .btn-loading {
        @apply relative pointer-events-none;
    }

    .btn-loading::after {
        content: '';
        @apply absolute inset-0 flex items-center justify-center bg-inherit rounded-xl;
    }

    .btn-loading::before {
        content: '';
        @apply absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-5 h-5 
               border-2 border-white/30 border-t-white rounded-full animate-spin z-10;
    }

    .hover-lift {
        @apply transition-all duration-300 ease-out;
    }

    .hover-lift:hover {
        @apply -translate-y-1;
        box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
    }

    .hover-scale {
        @apply transition-transform duration-200 ease-out;
    }

    .hover-scale:hover {
        @apply scale-[1.02];
    }

    .hover-glow {
        @apply transition-shadow duration-300;
    }

    .hover-glow:hover {
        box-shadow: 0 0 40px rgba(99, 102, 241, 0.15);
    }

    .shimmer {
        @apply relative overflow-hidden;
    }

    .shimmer::after {
        content: '';
        @apply absolute inset-0;
        background: linear-gradient(
            90deg,
            transparent,
            rgba(255, 255, 255, 0.4),
            transparent
        );
        animation: shimmer 2s infinite;
    }

    @keyframes shimmer {
        0% { transform: translateX(-100%); }
        100% { transform: translateX(100%); }
    }

    .dark .shimmer::after {
        background: linear-gradient(
            90deg,
            transparent,
            rgba(255, 255, 255, 0.08),
            transparent
        );
    }

    .skeleton {
        @apply bg-gray-200 dark:bg-gray-700 rounded-lg;
        animation: skeleton-pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
    }

    @keyframes skeleton-pulse {
        0%, 100% { opacity: 1; }
        50% { opacity: 0.5; }
    }

    .truncate-2 {
        display: -webkit-box;
        -webkit-line-clamp: 2;
        -webkit-box-orient: vertical;
        overflow: hidden;
    }

    .truncate-3 {
        display: -webkit-box;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        overflow: hidden;
    }

    .scrollbar-thin {
        scrollbar-width: thin;
        scrollbar-color: rgba(156, 163, 175, 0.5) transparent;
    }

    .scrollbar-thin::-webkit-scrollbar {
        width: 6px;
        height: 6px;
    }

    .scrollbar-thin::-webkit-scrollbar-track {
        background: transparent;
    }

    .scrollbar-thin::-webkit-scrollbar-thumb {
        background-color: rgba(156, 163, 175, 0.5);
        border-radius: 9999px;
    }

    .scrollbar-thin::-webkit-scrollbar-thumb:hover {
        background-color: rgba(156, 163, 175, 0.7);
    }

    .dark .scrollbar-thin {
        scrollbar-color: rgba(75, 85, 99, 0.5) transparent;
    }

    .dark .scrollbar-thin::-webkit-scrollbar-thumb {
        background-color: rgba(75, 85, 99, 0.5);
    }

    .dark .scrollbar-thin::-webkit-scrollbar-thumb:hover {
        background-color: rgba(75, 85, 99, 0.7);
    }

    .transition-dark {
        @apply transition-colors duration-300;
    }

    .glass {
        @apply bg-white/80 dark:bg-gray-800/80 backdrop-blur-xl;
    }

    .glass-dark {
        @apply bg-gray-900/80 backdrop-blur-xl;
    }

    .gradient-border {
        @apply relative;
    }

    .gradient-border::before {
        content: '';
        @apply absolute -inset-px rounded-xl z-[-1];
        background: linear-gradient(135deg, #6366f1, #8b5cf6, #ec4899);
    }

    .text-gradient {
        @apply bg-clip-text text-transparent bg-gradient-to-r from-primary-600 to-purple-600;
    }

    .rounded-inherit {
        border-radius: inherit;
    }

    .focus-ring {
        @apply focus:outline-none focus:ring-4 focus:ring-primary-500/20 focus:border-primary-500;
    }
}

@layer utilities {
    @media (max-width: 640px) {
        .mobile-card {
            @apply rounded-none border-x-0;
        }

        .mobile-stack {
            @apply flex flex-col gap-2;
        }

        .mobile-hide {
            @apply hidden;
        }

        .mobile-full-width {
            @apply w-full;
        }

        .mobile-text-sm {
            @apply text-sm;
        }

        .mobile-py-3 {
            @apply py-3;
        }
    }

    @media (max-width: 768px) {
        .tablet-stack {
            @apply flex flex-col gap-3;
        }

        .responsive-table-wrapper {
            @apply overflow-x-auto -mx-4 sm:mx-0;
        }

        .responsive-table {
            @apply min-w-[600px];
        }

        .tablet-full-width {
            @apply w-full;
        }
    }

    .touch-target {
        @apply min-h-[44px] min-w-[44px];
    }

    .safe-area-bottom {
        padding-bottom: env(safe-area-inset-bottom);
